import { formatDateToLocal, formatNumberToLocal, getIgv } from '@/lib/utils'
import { type QuotationClient } from '@/types'
import type { TDocumentDefinitions } from 'pdfmake/interfaces'
import { companyInfo } from './company-info'
import { getItemsTable } from './items-table'
import { getTermAndTotal } from './terms-and-total'

export function generateQuotationPdf(quotation: QuotationClient) {
  const pdfDoc: TDocumentDefinitions = {
    content: [
      // Company info
      companyInfo,
      // Customer Info
      {
        marginTop: 15,
        marginBottom: 15,
        columns: [
          {
            table: {
              widths: [35, 'auto', '*'],
              body: [
                ['Cliente', ':', quotation?.customer?.name || ''],
                ['Ruc', ':', quotation?.customer?.ruc || ''],
                ['Dirección', ':', quotation?.customer?.address || ''],
              ],
            },
            layout: {
              defaultBorder: false,
              paddingLeft: () => 0,
            },
          },
          {
            table: {
              widths: ['*', 'auto', 'auto', '*'],
              body: [
                [
                  {
                    text: 'Cotización #',
                    colSpan: 2,
                    alignment: 'right',
                  },
                  {},
                  ':',
                  {
                    text: `00${quotation.number}`,
                    bold: true,
                    alignment: 'right',
                    fontSize: 10,
                  },
                ],
                [
                  {
                    text: 'Fecha',
                    colSpan: 2,
                    alignment: 'right',
                  },
                  {},
                  ':',
                  {
                    text: formatDateToLocal(quotation.updatedAt),
                    alignment: 'right',
                  },
                ],
              ],
            },

            layout: {
              defaultBorder: false,
              paddingRight: () => 0,
            },
          },
        ],
      },
      // Table
      getItemsTable(quotation),
      // Term and coditions
      getTermAndTotal(quotation),
      {
        columns: [
          {
            stack: [
              {
                text: 'Cuenta Banco de Crédito del Perú (BCP) Soles',
              },
              { text: '19276743336019', bold: true, fontSize: 10 },
              {
                text: 'Cuenta interbancaria BCP (CCI) Soles',
                marginTop: 8,
              },
              {
                text: '00219217674333601938',
                fontSize: 10,
                bold: true,
              },
            ],
            columnGap: 20,
          },
          {
            stack: [
              {
                image:
                  'data:image/png;base64,',
                width: 65,
              },
              {
                text: 'Raquel Maldonado R.',
                decoration: 'underline',
              },
              {
                text: 'Gerente Comercial',
                fontSize: 7,
              },
            ],
            alignment: 'right',
          },
        ],
        marginTop: 25,
      },
    ],
    language: 'es-US',
    pageSize: 'A4',
    pageMargins: [20, 20],
    defaultStyle: {
      fontSize: 8,
      bold: false,
    },
    styles: {
      linkText: {
        decoration: 'underline',
        color: '#7d2de0',
        italics: true,
      },
    },
  }

  return pdfDoc
}
